mapper.insert:
	INSERT INTO QNA VALUES(
	       #{proNum}
    	 , (SELECT PRO_NAME FROM MAP WHERE PRO_NUM = #{proNum})
		 , #{memId}
	     , #{memQuestion}
		 , (SELECT MEM_AUTHORITY FROM MEMBER WHERE MEM_ID = #{memId})
		 , NULL
	     , SYSDATE
	)

mapper.select:
SELECT * FROM QNA
WHERE PRO_NUM = #{proNum}
ORDER BY QNA_DATE DESC
